<template>
     <tr>
        <td>{{user.id}}</td>
        <td>{{user.name}}</td>
        <td>{{user.surname}}</td>
        <td>{{user.phone}}</td>
        <td>{{user.email}}</td>
        <td>
            <img src="https://thumbs.dreamstime.com/b/%D1%80%D0%B5%D0%B4%D0%B0%D0%BA%D1%82%D0%B8%D1%80%D1%83%D0%B9%D1%82%D0%B5-%D0%B7%D0%BD%D0%B0%D1%87%D0%BE%D0%BA-%D0%BA%D0%B0%D1%80%D0%B0%D0%BD%D0%B4%D0%B0%D1%88%D0%B0-123950533.jpg" height="33" width="33" @click="goTo">
            <img src="data:image/png;base64," height="30" width="30" @click="remove">
        </td>
    </tr>
</template>

<script>

export default {
    props: ['user'],
    methods: {
        goTo() {
            let id = this.getId()

            this.$router.push({ name: 'EditUser', params: { id } })
        },
        remove() {
            let id = this.getId()
            this.$store.dispatch('removeUser', id)
        },
        getId() {
            let row = event.target.closest('tr')
            let col = row.getElementsByTagName('td')

            return col[0].innerHTML
        }
    }
}
</script>

<style>
td {
    border: 1px solid #333;
    border-collapse: collapse;
}
</style>